clear;clc;figure = figure;frame = 120x_max = 100y_max = 200axes = axes('color', 'white', 'XLim',[0 x_max], 'YLim',[0 y_max] , #Lim은 각 축의 범위'PlotBoxAspectRatio', [0.6 1 1], 'Position',[0 0.04 1 0.95]);     #PlotBoxAspectRatio 은 각 축의 상대적 길이x = 20;y = 50;size = 30ball = line(x,y,'marker','.','markersize',size)x = x+10;y = y+10;dx = 0.2dy = 0.2time = 0ball_term_sceond = 0.5;ball_term = frame * ball_term_sceond;function c = x_2 (x)  c=x+50;endfunctionprintf("%d",x_2(30));  while true  set(ball,'XData', x, 'YData', y);  x = x+dx;  y = y+dy;    if x>=x_max - size/10 || x<=0+size/10    dx = -1*dx;    end      if y>=y_max  || y<=0+size/10    dy = -1*dy;    end    time = time+1;    if rem(time, ball_term) == 0    new_ball = line(x,y,'marker','.','markersize',size, 'color' , 'red');    end
    pause(1/frame) end