clear;clc;figure = figure;axes = axes('color', 'white', 'XLim',[0 100], 'YLim',[0,200] , #Lim은 각 축의 범위'PlotBoxAspectRatio', [0.6 1 1], 'Position',[0 0.04 1 0.95]);     #PlotBoxAspectRatio 은 각 축의 상대적 길이x = 20;y = 50;size = 30ball = line(x,y,'marker','.','markersize',size)x = x+10;y = y+10;dx = 0.1dy = 0.1  while true  set(ball,'XData', x, 'YData', y);  x = x+dx;  y = y+dy;    if x>=100 - size/10 || x<=0+size/10    dx = -1*dx;    end      if y>=100  || y<=0+size/10    dy = -1*dy;    end      pause(0.001) end